import React, { useState } from "react";
import { FaPlus } from "react-icons/fa";
import * as S from "./styles";
import { useFormik } from 'formik';
import * as yup from 'yup';

export default function InputButton({ handleClick }) {
  const [text, setText] = useState("");
  const [price, setPrice] = useState("");

  const handleOnChangeText = (evt) => {
    setText(evt.target.value);
  };
  const handleOnChangePrice = (evt) => {
    setPrice(evt.target.value);
  };
  const buttonHandleClick = (evt) => {
    evt.preventDefault();
    handleClick(text, price);
    setText("");
    setPrice("");
  };

  //verificação de campos

  const schema = yup.object({
    text: yup.string().required('Título obrigatório!').max(100),
    price: yup.number().required('Preço obrigatório!').max(4),
})

const formik = useFormik({
  initialValues: {
      text,
      price,

  },
  validationSchema: schema,
  onSubmit: (values) => {
      console.log(values);
      setItem(item => [...item, values])
  },
})
  return (
    <S.Container >
      <form onSubmit={formik.handleSubmit}>
      <S.Input name="fruta" value={formik.values.fruta} onChange={formik.handleChange} placeholder={formik.errors.fruta}/>

      <S.Input name="preco" value={formik.values.preco} onChange={handleOnChangePrice}  placeholder="Preço da fruta"/>

      <S.Button color="#86B049" onClick={buttonHandleClick}>
        <FaPlus size={19} />
      </S.Button>
       </form>
    </S.Container>
  );
}
