import React, { useEffect, useState } from "react";
import { IoTrashOutline } from "react-icons/io5";
import * as S from "./styles";
import { ReactNotifications } from 'react-notifications-component';
import { Store } from 'react-notifications-component';
import "animate.css";
import 'react-notifications-component/dist/theme.css';

export default function ListItem({ item, onCheckClick }) {
  const [isChecked, setIsChecked] = useState(false);

  useEffect(() => {
    setIsChecked(item.checked);
  }, [item]);

  const handleInputChange = () => {
    setIsChecked(!item.checked);
    onCheckClick(item)
  }

  const onRemoveClick = () => {
    Store.addNotification({
      title: "Item deletado",
      message: "Você acaba de deletar um item",
      type: "success",
      insert: "top",
      container: "top-right",
      animationIn: ["animate__animated", "animate__fadeIn"],
      animationOut: ["animate__animated", "animate__fadeOut"],
      dismiss: {
        duration: 5000,
        onScreen: true
      }
    })
  }

  return (
    <S.Container>
       <ReactNotifications>
      <div>
        <S.Checkbox
          type="checkbox"
          checked={isChecked || false}
          onChange={handleInputChange}
        />

        <S.Label>Fruta: {item.title}</S.Label>
        <S.Label>Preço: {item.price}</S.Label>
      </div>


      <S.Button onClick={ onRemoveClick}>
        <IoTrashOutline size={22} />
      </S.Button>
     </ReactNotifications>  
    </S.Container>
  );
}
