import { iNotificationDismiss, iNotification, iNotificationCustomType, iTransition } from 'src/typings'
import { NOTIFICATION_BASE_CLASS } from "src/utils/constants"
import { NOTIFICATION_CONTAINER, NOTIFICATION_INSERTION, NOTIFICATION_TYPE } from "src/utils/enums"

export const isNullOrUndefined = (object: any) => object === null || object === undefined

export function isBottomContainer(container: string) {
  return (
    container === NOTIFICATION_CONTAINER.BOTTOM_FULL ||
    container === NOTIFICATION_CONTAINER.BOTTOM_LEFT ||
    container === NOTIFICATION_CONTAINER.BOTTOM_RIGHT ||
    container === NOTIFICATION_CONTAINER.BOTTOM_CENTER
  )
}

export function isTopContainer(container: string) {
  return (
    container === NOTIFICATION_CONTAINER.TOP_FULL ||
    container === NOTIFICATION_CONTAINER.TOP_LEFT ||
    container === NOTIFICATION_CONTAINER.TOP_RIGHT ||
    container === NOTIFICATION_CONTAINER.TOP_CENTER
  )
}

export function hasFullySwiped(diffX: number, width: number) {
  const swipeLength = Math.abs(diffX)
  const requiredSwipeLength = (40 / 100) * width
  return swipeLength >= requiredSwipeLength
}

export function shouldNotificationHaveSliding(notification: iNotification, count: number) {
  if (count <= 1) {
    return false
  }

  return (
    count > 1 &&
    ((notification.insert === NOTIFICATION_INSERTION.TOP && isTopContainer(notification.container)) ||
      (notification.insert === NOTIFICATION_INSERTION.BOTTOM && isBottomContainer(notification.container)) ||
      notification.container === NOTIFICATION_CONTAINER.CENTER)
  )
}

export function htmlClassesForExistingType(type: NOTIFICATION_TYPE | string) {
  switch (type) {
    case NOTIFICATION_TYPE.DEFAULT:
      return [NOTIFICATION_BASE_CLASS, 'rnc__notification-item--default']
    case NOTIFICATION_TYPE.SUCCESS:
      return [NOTIFICATION_BASE_CLASS, 'rnc__notification-item--success']
    case NOTIFICATION_TYPE.DANGER:
      return [NOTIFICATION_BASE_CLASS, 'rnc__notification-item--danger']
    case NOTIFICATION_TYPE.WARNING:
      return [NOTIFICATION_BASE_CLASS, 'rnc__notification-item--warning']
    case NOTIFICATION_TYPE.INFO:
      return [NOTIFICATION_BASE_CLASS, 'rnc__notification-item--info']
    default:
      return [NOTIFICATION_BASE_CLASS]
  }
}

export function getHtmlClassesForType(notification: iNotification) {
  const { type, content, userDefinedTypes } = notification
  const base = [NOTIFICATION_BASE_CLASS]
  if (content) {
    return base
  }

  if (isNullOrUndefined(userDefinedTypes)) {
    return htmlClassesForExistingType(type)
  }

  const foundType = userDefinedTypes.find((q) => q.name === type)
  return base.concat(foundType.htmlClasses)
}

export function getNotificationsForMobileView(notifications: iNotification[]) {
  const top: iNotification[] = []
  const bottom: iNotification[] = []

  notifications.forEach((notification) => {
    const { container } = notification
    const { CENTER } = NOTIFICATION_CONTAINER

    if (isTopContainer(container) || container === CENTER) {
      top.push(notification)
    } else if (isBottomContainer(container)) {
      bottom.push(notification)
    }
  })

  return { top, bottom }
}

export function getNotificationsForEachContainer(notifications: iNotification[]) {
  const topLeft: iNotification[] = []
  const topRight: iNotification[] = []
  const topCenter: iNotification[] = []
  const bottomLeft: iNotification[] = []
  const bottomRight: iNotification[] = []
  const bottomCenter: iNotification[] = []
  const center: iNotification[] = []
  const topFull: iNotification[] = []
  const bottomFull: iNotification[] = []

  notifications.forEach((notification) => {
    const { container } = notification
    if (container === NOTIFICATION_CONTAINER.TOP_FULL) {
      topFull.push(notification)
    } else if (container === NOTIFICATION_CONTAINER.BOTTOM_FULL) {
      bottomFull.push(notification)
    } else if (container === NOTIFICATION_CONTAINER.TOP_LEFT) {
      topLeft.push(notification)
    } else if (container === NOTIFICATION_CONTAINER.TOP_RIGHT) {
      topRight.push(notification)
    } else if (container === NOTIFICATION_CONTAINER.TOP_CENTER) {
      topCenter.push(notification)
    } else if (container === NOTIFICATION_CONTAINER.BOTTOM_LEFT) {
      bottomLeft.push(notification)
    } else if (container === NOTIFICATION_CONTAINER.BOTTOM_RIGHT) {
      bottomRight.push(notification)
    } else if (container === NOTIFICATION_CONTAINER.BOTTOM_CENTER) {
      bottomCenter.push(notification)
    } else if (container === NOTIFICATION_CONTAINER.CENTER) {
      center.push(notification)
    }
  })

  return {
    topFull,
    bottomFull,
    topLeft,
    topRight,
    topCenter,
    bottomLeft,
    bottomRight,
    bottomCenter,
    center
  }
}

export function getTransition({ duration, timingFunction, delay }: iTransition, property: string) {
  return `${duration}ms ${property} ${timingFunction} ${delay}ms`
}

export function getUid(a?: string): string {
  return a ? (0 | (Math.random() * 16)).toString(16) : ('' + 1e11 + 1e11).replace(/1|0/g, getUid)
}

function defaultTransition(
  transition: iTransition,
  { duration, timingFunction, delay }: iTransition
) {
  const transitionOptions = transition || ({} as iTransition)

  if (isNullOrUndefined(transitionOptions.duration)) {
    transitionOptions.duration = duration
  }

  if (isNullOrUndefined(transitionOptions.timingFunction)) {
    transitionOptions.timingFunction = timingFunction
  }

  if (isNullOrUndefined(transitionOptions.delay)) {
    transitionOptions.delay = delay
  }

  return transitionOptions
}

function defaultDismiss(dismiss: iNotificationDismiss): iNotificationDismiss {
  const option = dismiss
  const defaults: iNotificationDismiss = {
    duration: 0,
    click: true,
    touch: true,
    onScreen: false,
    pauseOnHover: false,
    waitForAnimation: false,
    showIcon: false
  }

  if (!option) {
    return defaults
  }

  Object.keys(defaults).forEach((prop) => {
    if (isNullOrUndefined(option[prop])) {
      option[prop] = defaults[prop]
    }
  })

  return option
}

function defaultUserDefinedTypes(
  notification: iNotification,
  definedTypes: iNotificationCustomType[]
) {
  const { content, type } = notification
  if (content) {
    return undefined
  }

  if (
    type === NOTIFICATION_TYPE.SUCCESS ||
    type === NOTIFICATION_TYPE.DANGER ||
    type === NOTIFICATION_TYPE.INFO ||
    type === NOTIFICATION_TYPE.DEFAULT ||
    type === NOTIFICATION_TYPE.WARNING ||
    !definedTypes
  ) {
    return undefined
  }

  return definedTypes
}

export function parseNotification(
  options: iNotification,
  userDefinedTypes: iNotificationCustomType[],
  defaultNotificationWidth: number
): iNotification {
  const notification = options
  const {
    id,
    type,
    insert,
    content,
    container,
    animationIn,
    animationOut,
    slidingEnter,
    slidingExit,
    touchRevert,
    touchSlidingExit,
    dismiss,
    width,
    onRemoval
  } = notification

  notification.id = id || getUid()
  notification.type = content ? null : (type.toLowerCase() as NOTIFICATION_TYPE)

  if (userDefinedTypes && !content) {
    notification.userDefinedTypes = defaultUserDefinedTypes(notification, userDefinedTypes)
  }

  notification.width = isNullOrUndefined(width) ? defaultNotificationWidth : width
  notification.container = container.toLowerCase() as NOTIFICATION_CONTAINER
  notification.insert = (insert || 'top').toLowerCase() as NOTIFICATION_INSERTION
  notification.dismiss = defaultDismiss(dismiss)
  notification.animationIn = animationIn || []
  notification.animationOut = animationOut || []

  // eslint-disable-next-line
  notification.onRemoval = onRemoval || (() => {})

  const t = (duration: number, timingFunction: string, delay: number) => ({
    duration,
    timingFunction,
    delay
  })

  notification.slidingEnter = defaultTransition(slidingEnter, t(600, 'linear', 0))
  notification.slidingExit = defaultTransition(slidingExit, t(600, 'linear', 0))
  notification.touchRevert = defaultTransition(touchRevert, t(600, 'linear', 0))

  const touchExit = touchSlidingExit || ({} as { fade: iTransition; swipe: iTransition })
  const swipe = touchExit.swipe || ({} as iTransition)
  const fade = touchExit.fade || ({} as iTransition)
  notification.touchSlidingExit = touchExit
  notification.touchSlidingExit.swipe = defaultTransition(swipe, t(600, 'linear', 0))
  notification.touchSlidingExit.fade = defaultTransition(fade, t(300, 'linear', 0))

  return notification
}

export default class Timer {
  constructor(callback: () => void, delay: number) {
    this.callback = callback
    this.remaining = delay

    this.resume()
  }

  private timerId: NodeJS.Timeout
  private start: number
  private remaining: number
  private callback: () => void

  public pause() {
    clearTimeout(this.timerId)
    this.remaining -= Date.now() - this.start
  }

  resume() {
    this.start = Date.now()
    clearTimeout(this.timerId)
    this.timerId = setTimeout(this.callback, this.remaining)
  }

  clear() {
    clearTimeout(this.timerId)
  }
}

import React from 'react'
import { iNotificationProps, iNotificationState, iNotificationParentStyle } from 'src/typings'
import {
  getHtmlClassesForType,
  getTransition,
  hasFullySwiped,
  shouldNotificationHaveSliding
} from 'src/utils/helpers'
import Timer from 'src/utils/timer'
import { NOTIFICATION_REMOVAL_SOURCE } from 'src/utils/enums'

export { Notification }

class Notification extends React.Component<iNotificationProps, iNotificationState> {
  constructor(props: iNotificationProps) {
    super(props)
    this.rootElementRef = React.createRef()

    const { defaultNotificationWidth, notification, isMobile } = props
    const { width } = notification

    this.state = {
      parentStyle: {
        height: `0px`,
        overflow: 'hidden',
        width: `${width ? width : defaultNotificationWidth}px`
      },
      htmlClassList: getHtmlClassesForType(notification),
      animationPlayState: 'running',
      touchEnabled: true
    }

    if (isMobile) {
      this.state.parentStyle.width = '100%'
    }
  }

  private readonly rootElementRef: React.RefObject<HTMLDivElement>
  private timer: Timer

  componentWillUnmount() {
    if (this.timer) {
      this.timer.clear()
    }
  }

  componentDidMount() {
    const { notification, notificationsCount } = this.props
    const {
      dismiss: { duration, onScreen }
    } = notification
    const willSlide = shouldNotificationHaveSliding(notification, notificationsCount)
    const { scrollHeight } = this.rootElementRef.current

    const onTransitionEnd = () => {
      if (!duration || onScreen) return
      const callback = () => this.removeNotification(NOTIFICATION_REMOVAL_SOURCE.TIMEOUT)
      this.timer = new Timer(callback, duration)
    }

    const callback = () => {
      requestAnimationFrame(() => {
        this.setState((prevState) => ({
          htmlClassList: [...notification.animationIn, ...prevState.htmlClassList]
        }))
      })
    }

    this.setState(
      ({ parentStyle: { width } }) => ({
        parentStyle: {
          width,
          height: `${scrollHeight}px`,
          transition: willSlide ? getTransition(notification.slidingEnter, 'height') : '10ms height'
        },
        onTransitionEnd
      }),
      callback
    )
  }

  componentDidUpdate(prevProps: iNotificationProps) {
    if (this.props.hasBeenRemoved && !prevProps.hasBeenRemoved) {
      this.removeNotification(NOTIFICATION_REMOVAL_SOURCE.MANUAL)
    }

    if (prevProps !== this.props) {
      const { container } = this.props.notification
      const { scrollHeight } = this.rootElementRef.current.children[0]

      this.setState(({ parentStyle }) => ({
        parentStyle: {
          ...parentStyle,
          height: `${scrollHeight + (container.endsWith('full') ? 0 : 15)}px`
        }
      }))
    }
  }

  removeNotification(removalFlag: string) {
    const { notification, toggleRemoval } = this.props
    const {
      id,
      onRemoval,
      dismiss: { waitForAnimation }
    } = notification

    const htmlClassList = [...notification.animationOut, ...getHtmlClassesForType(notification)]
    const onTransitionEnd = () => toggleRemoval(id, () => onRemoval(id, removalFlag))
    const parentStyle: iNotificationParentStyle = {
      height: `0px`,
      overflow: 'hidden',
      transition: getTransition(notification.slidingExit, 'height')
    }

    if (waitForAnimation) {
      return this.setState(({ parentStyle: { width } }) => ({
        htmlClassList,
        onAnimationEnd: () => {
          this.setState({
            parentStyle: {
              width,
              ...parentStyle
            },
            onTransitionEnd
          })
        }
      }))
    }

    return this.setState(({ parentStyle: { width } }) => ({
      parentStyle: {
        width,
        ...parentStyle
      },
      onTransitionEnd,
      htmlClassList
    }))
  }

  onClick = () => {
    const {
      notification: { dismiss }
    } = this.props
    if (dismiss.click || dismiss.showIcon) {
      this.removeNotification(NOTIFICATION_REMOVAL_SOURCE.CLICK)
    }
  }

  onTouchStart = (event: React.TouchEvent<HTMLDivElement>) => {
    const { pageX } = event.touches[0]

    this.setState(({ parentStyle }) => ({
      startX: pageX,
      currentX: pageX,
      parentStyle: {
        ...parentStyle,
        position: 'relative'
      }
    }))
  }

  onTouchMove = (event: React.TouchEvent) => {
    const { pageX } = event.touches[0]
    const { startX } = this.state
    const {
      toggleRemoval,
      notification: {
        id,
        onRemoval,
        slidingExit,
        touchSlidingExit: { swipe, fade }
      }
    } = this.props

    const distance = pageX - startX
    const { offsetWidth: width } = this.rootElementRef.current
    const swipeTo = window.innerWidth + width
    const left = `${pageX - startX >= 0 ? swipeTo : -swipeTo}px`

    if (hasFullySwiped(distance, width)) {
      const t1 = getTransition(swipe, 'left')
      const t2 = getTransition(fade, 'opacity')
      const onTransitionEnd = () => {
        toggleRemoval(id, () => onRemoval(id, NOTIFICATION_REMOVAL_SOURCE.TOUCH))
      }

      return this.setState(({ parentStyle }) => ({
        touchEnabled: false,
        parentStyle: {
          ...parentStyle,
          left,
          opacity: 0,
          transition: `${t1}, ${t2}`
        },
        onTransitionEnd: () => {
          this.setState(({ parentStyle }) => ({
            parentStyle: {
              ...parentStyle,
              height: `0px`,
              overflow: 'hidden',
              transition: getTransition(slidingExit, 'height')
            },
            onTransitionEnd
          }))
        }
      }))
    }

    return this.setState(({ parentStyle }) => ({
      currentX: pageX,
      parentStyle: {
        ...parentStyle,
        left: `${0 + distance}px`
      }
    }))
  }

  onTouchEnd = () => {
    const {
      notification: { touchRevert }
    } = this.props

    this.setState(({ parentStyle }) => ({
      parentStyle: {
        ...parentStyle,
        left: 0,
        transition: getTransition(touchRevert, 'left')
      }
    }))
  }

  onMouseEnter = () => {
    if (this.timer) {
      this.timer.pause()
    } else {
      this.setState({ animationPlayState: 'paused' })
    }
  }

  onMouseLeave = () => {
    if (this.timer) {
      this.timer.resume()
    } else {
      this.setState({ animationPlayState: 'running' })
    }
  }

  renderTimer() {
    const {
      notification: { dismiss }
    } = this.props
    const { duration, onScreen } = dismiss
    const { animationPlayState } = this.state

    if (!duration || !onScreen) {
      return
    }

    const style = {
      animationName: 'timer',
      animationDuration: `${duration}ms`,
      animationTimingFunction: 'linear',
      animationFillMode: 'forwards',
      animationDelay: `0`,
      animationPlayState
    }

    const onAnimationEnd = () => this.removeNotification(NOTIFICATION_REMOVAL_SOURCE.TIMEOUT)
    return (
      <div className="rnc__notification-timer">
        <div
          className="rnc__notification-timer-filler"
          onAnimationEnd={onAnimationEnd}
          style={style}
        ></div>
      </div>
    )
  }

  renderCustomContent() {
    const { htmlClassList } = this.state
    const {
      notification: {
        id,
        content: CustomContent,
        dismiss: { duration, pauseOnHover }
      }
    } = this.props

    const hasMouseEvents = duration > 0 && pauseOnHover

    return (
      <div
        className={`${[...htmlClassList].join(' ')}`}
        onMouseEnter={hasMouseEvents ? this.onMouseEnter : null}
        onMouseLeave={hasMouseEvents ? this.onMouseLeave : null}
      >
        {React.isValidElement(CustomContent) ? CustomContent : <CustomContent {...{ id }} />}
      </div>
    )
  }

  renderNotification() {
    const {
      notification: {
        title,
        message,
        dismiss: { showIcon, duration, pauseOnHover }
      }
    } = this.props
    const { htmlClassList } = this.state
    const hasMouseEvents = duration > 0 && pauseOnHover

    return (
      <div
        className={`${[...htmlClassList].join(' ')}`}
        onMouseEnter={hasMouseEvents ? this.onMouseEnter : null}
        onMouseLeave={hasMouseEvents ? this.onMouseLeave : null}
      >
        <div className="rnc__notification-content">
          {showIcon && <div className="rnc__notification-close-mark" onClick={this.onClick}></div>}
          {title && <div className="rnc__notification-title">{title}</div>}
          <div className="rnc__notification-message">{message}</div>
          {this.renderTimer()}
        </div>
      </div>
    )
  }

  render() {
    const {
      notification: {
        content,
        dismiss: { click }
      }
    } = this.props
    const { parentStyle, onAnimationEnd, onTransitionEnd, touchEnabled } = this.state

    return (
      <div
        ref={this.rootElementRef}
        onClick={click ? this.onClick : null}
        className={'notification'}
        style={parentStyle}
        onAnimationEnd={onAnimationEnd}
        onTransitionEnd={onTransitionEnd}
        onTouchStart={touchEnabled ? this.onTouchStart : null}
        onTouchMove={touchEnabled ? this.onTouchMove : null}
        onTouchEnd={touchEnabled ? this.onTouchEnd : null}
      >
        {content ? this.renderCustomContent() : this.renderNotification()}
      </div>
    )
  }
}

import { iNotification, iNotificationCustomType, iNotificationStoreParams, iStore } from 'src/typings'
import { parseNotification } from 'src/utils/helpers'
import { validateTransition, validators } from 'src/utils/validators'

class Store implements iStore {
  constructor() {
    this.counter = 0
    this.add = null
  }

  public removeNotification: (id: string) => void
  public removeAllNotifications: () => void

  private types: iNotificationCustomType[]
  private add: (notification: iNotification) => string
  private defaultNotificationWidth: number
  private counter: number

  private incrementCounter = () => (this.counter += 1)

  public addNotification(notification: iNotification) {
    if (process.env.NODE_ENV === 'development') {
      const transitions = ['slidingEnter', 'slidingExit', 'touchRevert', 'touchSlidingExit']
      transitions.forEach((transition) => validateTransition(notification, transition))
      validators.forEach((validator) => validator(notification, this.types))
    }

    this.incrementCounter()

    const parsedNotification = parseNotification(
      notification,
      this.types,
      this.defaultNotificationWidth
    )


    return this.add(parsedNotification)
  }

  public getCounter = () => this.counter

  public register(parameters: iNotificationStoreParams) {
    const {
      addNotification,
      removeNotification,
      removeAllNotifications,
      types,
      defaultNotificationWidth
    } = parameters

    this.add = addNotification
    this.removeNotification = removeNotification
    this.removeAllNotifications = removeAllNotifications
    this.defaultNotificationWidth = defaultNotificationWidth
    this.types = types
  }
}

export default new Store()

import React from 'react'
import { iNotificationCustomType, iNotification } from 'src/typings'
import {Notification as ReactNotification} from 'src/components/Notification'
import store from 'src/store'
import { DEFAULT_CONTAINER_VALUES as DCV } from 'src/utils/constants'
import {
  getNotificationsForEachContainer,
  getNotificationsForMobileView,
  isNullOrUndefined
} from 'src/utils/helpers'

import "src/scss/notification.scss"

export { Container, iContainerProps, iContainerState }


interface iContainerProps {
  isMobile?: boolean
  breakpoint?: number
  types?: iNotificationCustomType[]
  defaultNotificationWidth?: number
}

interface iContainerState {
  isMobile: boolean
  breakpoint: number
  notifications: iNotification[]
  windowWidth: number
}

class Container extends React.Component<iContainerProps, iContainerState> {
  constructor(props: iContainerProps) {
    super(props)

    this.state = {
      isMobile: isNullOrUndefined(props.isMobile) ? DCV.isMobile : props.isMobile,
      breakpoint: isNullOrUndefined(props.breakpoint) ? DCV.breakpoint : props.breakpoint,
      notifications: [],
      windowWidth: undefined
    }
  }

  componentDidMount() {
    const { types, defaultNotificationWidth } = this.props

    store.register({
      addNotification: this.add,
      removeNotification: this.remove,
      removeAllNotifications: this.removeAllNotifications,
      defaultNotificationWidth: defaultNotificationWidth || DCV.defaultNotificationWidth,
      types
    })

    this.setState({ windowWidth: window.innerWidth })
    window.addEventListener('resize', this.handleResize)
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.handleResize)
  }

  handleResize = () => {
    this.setState({ windowWidth: window.innerWidth })
  }

  add = (notification: iNotification) => {
    this.setState(({ notifications }) => {
      const nextNotifications = [...notifications]
      const i = nextNotifications.findIndex(({ id }) => id === notification.id)

      if (i > -1) {
        nextNotifications[i] = notification

        return {
          notifications: nextNotifications
        }
      }

      return {
        notifications:
          notification.insert === 'top'
            ? [notification, ...nextNotifications]
            : [...nextNotifications, notification]
      }
    })

    return notification.id
  }

  remove = (id: string) => {
    this.setState(({ notifications }) => ({
      notifications: notifications.map((notification) => {
        if (notification.id === id) {
          notification.hasBeenRemoved = true
        }

        return notification
      })
    }))
  }

  removeAllNotifications = () => {
    this.setState({
      notifications: this.state.notifications.map((notification) => ({
        ...notification,
        hasBeenRemoved: true
      }))
    })
  }

  toggleRemoval = (id: string, callback: () => void) => {
    this.setState(
      ({ notifications }) => ({
        notifications: notifications.filter(({ id: nId }) => nId !== id)
      }),
      callback
    )
  }

  renderNotifications(notifications: iNotification[], isMobile: boolean) {
    return notifications.map((notification) => (
      <ReactNotification
        id={notification.id}
        key={notification.id}
        isMobile={isMobile}
        defaultNotificationWidth={this.props.defaultNotificationWidth}
        notification={notification}
        toggleRemoval={this.toggleRemoval}
        notificationsCount={notifications.length}
        hasBeenRemoved={notification.hasBeenRemoved}
      />
    ))
  }

  renderMobileNotifications(props) {
    const { className, id } = props
    const { notifications } = this.state
    const mobileNotifications = getNotificationsForMobileView(notifications)
    const top = this.renderNotifications(mobileNotifications.top, true)
    const bottom = this.renderNotifications(mobileNotifications.bottom, true)

    return (
      <div id={id} key="mobile" className={`rnc__base ${className || ''}`}>
        <div className="rnc__notification-container--mobile-top">{top}</div>
        <div className="rnc__notification-container--mobile-bottom">{bottom}</div>
      </div>
    )
  }

  renderScreenNotifications(props) {
    const { className, id } = props
    const { notifications } = this.state
    const items = getNotificationsForEachContainer(notifications)
    const topFull = this.renderNotifications(items.topFull, false)
    const bottomFull = this.renderNotifications(items.bottomFull, false)
    const topLeft = this.renderNotifications(items.topLeft, false)
    const topRight = this.renderNotifications(items.topRight, false)
    const topCenter = this.renderNotifications(items.topCenter, false)
    const bottomLeft = this.renderNotifications(items.bottomLeft, false)
    const bottomRight = this.renderNotifications(items.bottomRight, false)
    const bottomCenter = this.renderNotifications(items.bottomCenter, false)
    const center = this.renderNotifications(items.center, false)

    return (
      <div id={id} key="screen" className={`rnc__base ${className || ''}`}>
        <div className="rnc__notification-container--top-full">{topFull}</div>
        <div className="rnc__notification-container--bottom-full">{bottomFull}</div>
        <div className="rnc__notification-container--top-left">{topLeft}</div>
        <div className="rnc__notification-container--top-right">{topRight}</div>
        <div className="rnc__notification-container--bottom-left">{bottomLeft}</div>
        <div className="rnc__notification-container--bottom-right">{bottomRight}</div>
        <div className="rnc__notification-container--top-center">{topCenter}</div>
        <div className="rnc__notification-container--center">
          <div className="rnc__util--flex-center">{center}</div>
        </div>
        <div className="rnc__notification-container--bottom-center">{bottomCenter}</div>
      </div>
    )
  }

  render() {
    const { isMobile } = this.props
    const { windowWidth, breakpoint } = this.state

    if (isMobile && windowWidth <= breakpoint) {
      return this.renderMobileNotifications(this.props)
    }

    return this.renderScreenNotifications(this.props)
  }
}
